const express = require('express');
const dbC = require('./dbConfig');

const app = express();

// Define a route for handling button clicks
// Testing connection to AWS RDS. Last Tested 2/19 from Kaleb's computer
app.get('/button-click', async (req, res) => {
  try {
    dbC.pool.getConnection(function(err){
      if (err) throw err;
      dbC.pool.query("Select * from Users", function(err, results, fields) {
        if (err) throw err;
        console.log (results);
        res.json(results); // send results as JSON
      });
    });
  } catch (error) {
    console.error(error);
    console.log();
    res.status(500).send('Internal server error');
  }
});

// Start the server
app.listen(3000, () => {
  console.log('Server listening on port 3000');
});

module.exports = app;
